/* Text Thousands Separator under 1 B - preserves decimals, negatives */ /* Under 1 K */ IF( ROUND(Amount,0) < 1000, TEXT(ROUND(Amount,0)),
/* Under 1 M */ IF(ROUND(Amount,0) < 1000000, TEXT(FLOOR(ROUND(Amount,0) /1000)) & ' ' & RIGHT(TEXT(ROUND(Amount,0)), LEN(TEXT(ROUND(Amount,0))) - LEN(TEXT(FLOOR(ROUND(Amount,0) /1000)))),
/* Under 1 B */ IF(ROUND(Amount,0) < 1000000000, TEXT(FLOOR(ROUND(Amount,0) /1000000)) & ' ' & MID(TEXT(ROUND(Amount,0)), 1 + LEN(TEXT(FLOOR(ROUND(Amount,0) /1000000))),3) & ' ' & RIGHT(TEXT(ROUND(Amount,0)), LEN(TEXT(ROUND(Amount,0))) - LEN(TEXT(FLOOR(ROUND(Amount,0) /1000)))),
/* 1 B & up */ IF(ROUND(Amount,0) >= 1000000000, "#TooHigh!", NULL))))+
